mapper.update:
update sys_user
set userName = #{username,jdbcType=VARCHAR},
  pwd = #{pwd,jdbcType=VARCHAR},
  telephone = #{telephone,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select * from sys_user

	
		userName=#{username}
	
	
		and telephone=#{telephone}
	
	
		and sex=#{sex}
	


	limit  #{start},#{end}

	select count(1) from sys_user
    
    	
    		userName=#{username}
    	
    	
    		and telephone=#{telephone}
    	
    	
    		and sex=#{sex}
